export declare type MaybePromise<T> = Promise<T> | T;
export declare type MaybePromiseLike<T> = PromiseLike<T> | T;
export declare function isPromiseLike(value: any): value is PromiseLike<any>;
export declare function upgrade<T>(value: MaybePromiseLike<T>): MaybePromise<T>;
/**
 * A pledge is like a promise, except it runs synchronously if possible.
 */
export declare class Pledge<T> {
    private state;
    constructor(value: MaybePromiseLike<T>);
    then<TFulfilled = T, TRejected = never>(onFulfilled?: ((value: T) => MaybePromiseLike<TFulfilled>) | null, onRejected?: ((reason: any) => MaybePromiseLike<TRejected>) | null): MaybePromise<TFulfilled | TRejected>;
    catch<TRejected = never>(onRejected?: ((reason: any) => MaybePromiseLike<TRejected>) | null): MaybePromise<T | TRejected>;
    finally(onFinally?: (() => unknown) | null): MaybePromise<T>;
}
